import { useState, useEffect } from "react"
import { useNavigate, useParams} from 'react-router-dom'
import { createGame, getGameTypes, getGames, getGameById } from '../../managers/GameManager.js'


export const GameEdit = () => {
    const navigate = useNavigate()
    const {gameId} = useParams()
    const [gameTypes, setGameTypes] = useState([])

    /*
        Since the input fields are bound to the values of
        the properties of this state variable, you need to
        provide some default values.
    */
    const [currentGame, setCurrentGame] = useState()



    useEffect(() => {
        getGameTypes().then(setGameTypes)
      }, [])



    useEffect(() => {
        getGameById(gameId)
        .then(setCurrentGame)
        }, [gameId])



    const changeGameState = (domEvent) => {
        // TODO: Complete the onChange function
        // domEvent.preventDefault()
        // const newGame = Object.assign({}, currentGame)
        // newGame[domEvent.target.title] = domEvent.target.value
        // setCurrentGame(newGame)
        const copy = { ...currentGame }
        const propertyToModify = domEvent.target.id
        copy[propertyToModify] = domEvent.target.value
        setCurrentGame(copy)
        }

    return (
        <form className="gameForm">
            <h2 className="gameForm__title">Edit: {currentGame?.title}</h2>
            <fieldset>
                <div className="form-group">
                    <label htmlFor="title">Title: </label>
                    <input 
                        // onChange={
                        //     (evt) => {
                        //         const copy = {...currentGame}
                        //         copy.title = evt.target.value
                        //         setCurrentGame(copy)
                        //     }
                        // }
                        onChange={changeGameState}
                        type="text" id = 'title' required autoFocus className="form-control"
                        value = {currentGame?.title}
                    />
                </div>
            </fieldset>

            {/* TODO: create the rest of the input fields */}
            <fieldset>
                <div className="form-group">
                    <label htmlFor="maker">Maker: </label>
                    <input 
                    //  onChange={
                    //     (evt) => {
                    //         const copy = {...currentGame}
                    //         copy.maker = evt.target.value
                    //         setCurrentGame(copy)
                    //     }
                    // }
                    onChange={changeGameState}
                        type="text" id = 'maker' required autoFocus className="form-control"
                        value = {currentGame?.maker}
                    />
                </div>
            </fieldset>

            <fieldset>
                <div className="form-group">
                <label htmlFor="gameTypeId">Game Type: </label>
                <select 
                    onChange={changeGameState}
                    id="gameTypeId" className="form-control" value = {currentGame?.game_type?.label}
                    >
                    {
                    gameTypes.map((type) => {
                        return <option value={`${type.id}`} key={`type--${type.id}`}>{type.label}</option>
                    })
                    }
                </select>
                </div>
            </fieldset>

            <fieldset>
                <div className="form-group">
                    <label htmlFor="skillLevel">Skill Level: </label>
                    <input 
                    // onChange={
                    //     (evt) => {
                    //         const copy = {...currentGame}
                    //         copy.skillLevel = evt.target.value
                    //         setCurrentGame(copy)
                    //     }
                    // }
                        onChange={changeGameState}
                        type="number" name="skill_level" min = "1" max = "10" required autoFocus className="form-control"
                        id = 'skillLevel'
                    />
                </div>
            </fieldset>

            <fieldset>
                <div className="form-group">
                    <label htmlFor="numberOfPlayers">Number of players: </label>
                    <input 

                    onChange={changeGameState}
                        type="number" name="number_of_players" min = "1" max = "10" required autoFocus className="form-control"
                       id='numberOfPlayers'
                    />
                </div>
            </fieldset>

            <button type="submit"
                onClick={evt => {
                    // Prevent form from being submitted
                    evt.preventDefault()

                    const game = {
                        game_type: parseInt(currentGame.gameTypeId),
                        title: currentGame.title,
                        maker: currentGame.maker,
                        number_of_players: parseInt(currentGame.numberOfPlayers),
                        skill_level: parseInt(currentGame.skillLevel)
                    }

                    // Send POST request to your API
                    createGame(game)
                        .then(() => navigate("/games"))
                }}
                className="btn btn-primary">Create</button>
        </form>
    )
}
